import{_ as p}from"./plugin-vue_export-helper.21dcd24c.js";import{o as e,c as t,e as a}from"./app.14d8ac59.js";const r={},c=a('<h1 id="\u6211\u7684\u7B2C\u4E00\u4E2A-\u4E00\u5343\u4E07-\u77E5\u4E4E\u9605\u8BFB" tabindex="-1"><a class="header-anchor" href="#\u6211\u7684\u7B2C\u4E00\u4E2A-\u4E00\u5343\u4E07-\u77E5\u4E4E\u9605\u8BFB" aria-hidden="true">#</a> \u6211\u7684\u7B2C\u4E00\u4E2A\uFF0C\u4E00\u5343\u4E07\uFF01\u77E5\u4E4E\u9605\u8BFB</h1><p>\u5927\u5BB6\u597D\uFF0C\u6211\u662F\u4E8C\u54E5\u5440\uFF01</p><p>\u524D\u51E0\u5929\uFF0C\u5077\u5077\u6478\u6478\u8FC7\u4E86\u81EA\u5DF1\u7684\u7B2C N \u4E2A 18 \u5C81\uFF0C\u672C\u6765\u4E0D\u60F3\u8FC7\u751F\u65E5\u7684\uFF0C\u5C31\u60F3\u5F53\u505A\u662F\u5E73\u5E38\u7684\u4E00\u5929\u3002\u7ED3\u679C\u6211\u59B9\u975E\u8981\u63D0\u9192\u6211\uFF0C\u5927\u5BB6\u4F19\u77A7\u77A7\uFF0C\u8FD9\u50CF\u59B9\u59B9\u8BE5\u8BF4\u7684\u8BDD\u5417\uFF1F</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/szjy/zhihu-1000wan-5addb157-141f-400b-a51f-77557c8fdb8d.png" alt=""></p><p>\u545C\u545C\u545C~</p><p>\u6C42\u5B89\u6170\u3002</p><p>\u4E0D\u60F3\u5B89\u6170\u5C31\u60F3\u5077\u7740\u4E50\u662F\u5427\uFF1F\u90A3\u6211\u81EA\u5DF1\u5B89\u6170\u4E0B\u81EA\u5DF1\u5427\u3002</p><p><strong>\u7ECF\u8425\u4E86\u8FD1\u4E00\u5E74\u7684\u77E5\u4E4E\uFF0C\u9605\u8BFB\u603B\u6570\u7A81\u7834\u4E86\u4E00\u5343\u4E07\uFF0C\u8FD9\u4E5F\u662F\u6211\u4EBA\u751F\u5F53\u4E2D\u7684\u7B2C\u4E00\u4E2A</strong>\u3002</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/szjy/zhihu-1000wan-0324afde-4009-4e80-b878-2311ff88e5ca.png" alt=""></p><p>\u5176\u5B9E\u65E9\u5728 11 \u6708\u5C31\u7834\u4E86\u5343\u4E07\uFF0C\u5F53\u65F6\u5C31\u60F3\u8BB0\u5F55\u4E00\u4E0B\uFF0C\u4F46\u7EC6\u60F3\u4E00\u4E0B\uFF0C\u597D\u50CF\u8FD9\u70B9\u6210\u7EE9\u4E5F\u7B97\u4E0D\u4E0A\u4EC0\u4E48\u3002\u6BD5\u7ADF\u903C\u4E4E\u4E0A\u4EBA\u5747 985\u3001\u5E74\u85AA\u767E\u4E07\u3001\u521A\u4E0B\u98DE\u673A\u7684\u5927\u4F6C\u591A\u7684\u662F\u3002</p><p>\u4E3A\u4EC0\u4E48\u653E\u5728 12 \u6708\u8BF4\u5462\uFF0C\u5927\u5BB6\u90FD\u61C2\u7684\uFF0C\u6BD5\u7ADF\u518D\u8FC7\u51E0\u5929 2021 \u5E74\u5C31\u8981\u7ED3\u675F\u4E86\uFF0C\u603B\u5F97\u8BB0\u5F55\u70B9\u5565\u3002</p><p>\u51B5\u4E14\u4E00\u5343\u4E07\u7684\u9605\u8BFB\u91CF\uFF0C\u653E\u5728\u6280\u672F\u5708\uFF0C\u786E\u5B9E\u4E0D\u7B97\u5C11\u4E86\u3002\u51B5\u4E14\u6211\u8FD9\u4E9B\u5206\u4EAB\u786E\u5B9E\u5B9E\u6253\u5B9E\u7684\u5E2E\u52A9\u4E86\u4E0D\u5C11\u7684\u5C0F\u4F19\u4F34\u3002</p><p>\u8FD9\u4E0D\uFF0C\u524D\u51E0\u5929\u4E00\u4E2A\u5E16\u5B50\u83AB\u540D\u5176\u5999\u88AB\u77E5\u4E4E\u5220\u9664\u4E86\uFF0C\u6211\u662F\u65E0\u611F\u77E5\u7684\u3002\u4E00\u4E2A\u5C0F\u4F19\u4F34\u4E3A\u4E86\u770B\u8FD9\u4E2A\u5E16\u5B50\uFF0C\u8FD8\u7279\u610F\u53D1\u8D77\u4E86\u4E00\u6B21 9.8 \u5143\u7684\u4ED8\u8D39\u54A8\u8BE2\u3002</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/szjy/zhihu-1000wan-2fdd5b2b-67c5-40cf-b0e4-0a92a37e659a.png" alt=""></p><p>\u8FD9\u8DB3\u4EE5\u8BF4\u660E\u8FD9\u4E2A\u5E16\u5B50\u7684\u5185\u5BB9\u662F\u8DB3\u591F\u786C\u6838\u7684\u3002</p><p>\u5199\u77E5\u4E4E\u8FD9\u8FD1\u4E00\u5E74\u65F6\u95F4\u91CC\uFF0C\u6709\u4E00\u4E2A\u5E16\u5B50\u65E0\u58F0\u65E0\u606F\u5730\u7206\u4E86\uFF1A<strong>60 \u4E07+\u7684\u9605\u8BFB\uFF0C7000+\u8D5E\u540C\uFF0C2.3 \u4E07+\u6B21\u7684\u6536\u85CF</strong>\u3002</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/szjy/zhihu-1000wan-8b4637f2-08c9-479b-855f-3fd332d44651.png" alt=""></p><p>\u4E0D\u5BF9\u554A\uFF0C\u6536\u85CF\u7ADF\u7136\u662F\u70B9\u8D5E\u7684 3 \u500D\u8FD8\u591A\u3002\u3002\u55EF\uFF0C\u6B64\u65F6\u6B64\u523B\u5929\u7A7A\u98D8\u51FA\u6765\u4E86\u56DB\u4E2A\u5B57\uFF1A\u767D\u7968\u771F\u9999\u3002</p><p>\u4E0D\u8FC7\uFF0C\u6709\u4E00\u8BF4\u4E00\uFF0C\u5927\u5BB6\u597D\uFF0C\u624D\u662F\u771F\u7684\u597D\uFF0C\u8FD9\u4E48\u591A\u4EBA\u70B9\u8D5E\u6536\u85CF\uFF0C\u786E\u5B9E\u662F\u5E72\u8D27\u65E0\u7591\u4E86\u3002</p><p>\u8BB2\u771F\uFF0C\u8FD9\u4E2A\u5E16\u5B50\u8BF4\u5B9E\u5728\u7684\uFF0C\u5C5E\u4E8E\u65E0\u5FC3\u63D2\u67F3\u67F3\u6210\u836B\u4E86\uFF0C\u5C31\u662F\u4E00\u4E2A\u5373\u5174\u4F5C\u54C1\u3002</p><p>\u6211\u53EA\u4E0D\u8FC7\u662F\u628A\u81EA\u5DF1\u5199\u8FC7\u7684\u516C\u4F17\u53F7\u6587\u7AE0\u505A\u4E86\u4E00\u4E2A\u68B3\u7406\u603B\u7ED3\u3002\u4E0D\u8FC7\u6211\u8FC7\u4E86\u4E00\u628A\u5BFC\u6F14\u7684\u68A6\uFF0C\u52A0\u4E0A\u4E86\u4E00\u70B9\u7535\u5F71\u62CD\u6444\u7684\u624B\u6CD5\u3002</p><p>\u76F8\u4FE1\u5927\u5BB6\u53BB\u770B\u5B8C\u540E\u4E5F\u4F1A\u6709\u4E00\u79CD\uFF1A\u5367\u69FD\uFF0C\u725B\u903C\u554A\u3002</p><p>\u5E76\u4E14\u662F\u62CD\u7740\u5927\u817F\u7684\u90A3\u79CD\u3002</p><p>\u5730\u5740\u6211\u8D34\u51FA\u6765\u4E86\uFF0C\u6CA1\u770B\u8FC7\u7684\u5C0F\u4F19\u4F34\u53EF\u4EE5\u53BB\u6B23\u8D4F\u4E00\u4E0B\u3002</p><blockquote><p>https://www.zhihu.com/question/66535555/answer/1799868707</p></blockquote><p>\u4E0D\u77E5\u4E0D\u89C9\uFF0C\u6211\u5DF2\u7ECF\u56DE\u6D1B\u9633 8 \u5E74\u4E86\uFF01\u52A0\u4E0A\u5728\u82CF\u5DDE\u7684 3 \u5E74\u534A\uFF0C\u6B63\u513F\u516B\u7ECF\u505A\u7A0B\u5E8F\u5458\u7684\u65F6\u95F4\u5DF2\u7ECF\u8D85\u8FC7 10 \u5E74\u4E86\uFF0C\u65F6\u95F4\u8FC7\u5F97\u53EF\u771F\u5FEB\uFF01</p><p>\u4E3A\u4E86\u4F53\u73B0\u51FA\u6211\u7684\u6587\u5B66\u4FEE\u517B\uFF0C\u6B64\u65F6\u5FC5\u987B\u52A0\u4E0A\u51E0\u4E2A\u6210\u8BED\uFF1A\u771F\u7684\u662F\u5149\u9634\u4F3C\u7BAD\u3001\u65E5\u6708\u5982\u68AD\u3001\u767D\u9A79\u8FC7\u9699\u554A\u3002</p><p>\u6309\u7167\u7F51\u4E0A\u7684\u8BF4\u6CD5\uFF0C\u6211\u8FD9\u79CD\u4EBA\u8981\u4E48\u98DE\u9EC4\u817E\u8FBE\u8D22\u52A1\u81EA\u7531\u4E86\uFF0C\u8981\u4E48\u88AB\u65E0\u60C5\u8F9E\u9000\u8F6C\u884C\u8DD1\u6EF4\u6EF4\u4E86\u3002</p><p>\u65E0\u5948\u6211\u65E2\u4E0D\u662F\u524D\u4E00\u79CD\uFF0C\u4E5F\u4E0D\u662F\u540E\u4E00\u79CD\u3002</p><p>\u65E2\u4E0D\u5377\uFF0C\u4E5F\u4E0D\u8EBA\u5E73\u3002</p><p>\u6709\u7126\u8651\uFF0C\u4E5F\u6709\u5E78\u798F\u3002</p><p>\u5BF9\u672A\u6765\uFF0C\u6709\u671F\u5F85\uFF0C\u4E5F\u6709\u62C5\u5FE7\u3002</p><p>\u5BF9\u5F53\u4E0B\uFF0C\u6709\u6EE1\u8DB3\uFF0C\u4E5F\u6709\u4E0D\u6EE1\u3002</p><p>\u3002\u3002\u3002\u3002\u3002\u3002</p><p>\u5B8C\u86CB\uFF0C\u4E00\u4E0D\u5C0F\u5FC3\u4E0A\u5347\u5230\u54F2\u5B66\u7684\u5C42\u9762\u4E0A\u4E86\uFF0C\u6536\u6536\u6536\u6536\u3002</p><p>\u8FD9\u4E48\u8BF4\u5427\uFF0C\u6211\u4E00\u76F4\u89C9\u5F97\u81EA\u5DF1\u6BD4\u8F83\u5E78\u8FD0\u3002</p><p>\u5927\u5B66\u539F\u672C\u9009\u7684\u4E0D\u662F\u8BA1\u7B97\u673A\u4E13\u4E1A\uFF0C\u5374\u4E00\u4E0D\u5C0F\u5FC3\u505A\u4E86\u7A0B\u5E8F\u5458\uFF0C\u6CA1\u60F3\u5230\uFF0C\u8FD9\u4E2A\u804C\u4E1A\u8FD8\u633A\u9002\u5408\u81EA\u5DF1\u3002</p><p>\u5927\u5B66\u8FC7\u5F97\u4E00\u584C\u7CCA\u6D82\uFF0C\u5374\u4E0D\u7ECF\u610F\u95F4\u53BB\u4E86\u4E00\u5BB6\u5916\u4F01\uFF0C\u505A\u5230\u4E86 work-life-balance\u3002</p><p>\u8EAB\u5904\u4E09\u7EBF\u57CE\u5E02\uFF0C\u6309\u7406\u8BF4\u8FD9\u91CC\u662F\u4E00\u7247\u4E92\u8054\u7F51\u7684\u8352\u6F20\uFF0C\u6211\u5374\u5728\u8FD9\u91CC\u5F00\u8352\u62D3\u571F\u51FA\u4E86\u4E00\u7247\u7EFF\u6D32\u3002</p><p>\u518D\u52A0\u4E0A\u516C\u4F17\u53F7\u7684\u6070\u996D\uFF0C\u786E\u5B9E\u8BA9\u6211\u7684\u751F\u6D3B\u53D8\u5F97\u4E0D\u518D\u90A3\u4E48\u7A98\u8FEB\u4E86\u3002\u4E0D\u7BA1\u662F\u8F93\u5165\u8FD8\u662F\u8F93\u51FA\uFF0C\u4E5F\u90FD\u6709\u4E86\u66F4\u5F3A\u7684\u52A8\u529B\u3002</p><p>\u6BD5\u7ADF\u53EA\u9760\u7231\u6765\u53D1\u7535\uFF0C\u5F88\u96BE\u575A\u6301\u3002</p><p>\u5982\u679C\u4F60\u89C9\u5F97\u6709\u4E9B\u996D\u4E0D\u9519\uFF0C\u611F\u5174\u8DA3\uFF0C\u4E0D\u59A8\u8BD5\u8BD5\uFF0C\u7ED9\u81EA\u5DF1\u7684\u6295\u8D44\u6C38\u8FDC\u90FD\u662F\u503C\u5F97\u7684\uFF0C\u5E76\u4E14\u56DE\u62A5\u7387\u6781\u9AD8\u3002</p><p>\u53CD\u6B63\u6211\u81EA\u5DF1\u79C1\u5E95\u4E0B\u662F\u4E70\u4E86\u4E0D\u5C11\u8BFE\uFF0C\u6BD5\u7ADF\u5B66\u4E60\u662F\u6C38\u8FDC\u4E0D\u80FD\u843D\u4E0B\u7684\uFF0C\u4E0D\u7BA1\u4F60\u662F\u5728\u4E00\u7EBF\u57CE\u5E02\uFF0C\u8FD8\u662F\u4E8C\u4E09\u7EBF\u57CE\u5E02\u3002</p><p>\u7ED9\u5927\u5BB6\u770B\u4E00\u4E0B\uFF0C\u8FD9\u53EA\u662F\u6211\u4E66\u67DC\u7684\u4E00\u89D2\u3002</p><p>\u7EB8\u8D28\u4E66\u867D\u7136\u5DF2\u7ECF\u4E0D\u6D41\u884C\u4E86\uFF0C\u4F46\u5B83\u4F1A\u8BA9\u4F60\u611F\u89C9\u8E0F\u5B9E\uFF0C\u65E5\u5B50\u8FC7\u5F97\u6709\u5954\u5934\u3002</p><p>\u5BF9\u6070\u7684\u996D\u4E0D\u611F\u5174\u8DA3\u7684\uFF0C\u5FFD\u7565\u5C31\u597D\uFF0C\u591A\u770B\u770B\u5E72\u8D27\u7ED9\u4E2A\u4E09\u8FDE\u4E5F\u662F\u975E\u5E38\u4E0D\u9519\u7684\u4F53\u9A8C\u3002</p><p>\u6211\u5728\u77E5\u4E4E\u4E0A\u8FD8\u6709\u4E0D\u5C11\u786C\u6838\u8F93\u51FA\uFF0C\u5C24\u5176\u662F\u8FD9\u4E9B\u5343\u8D5E\u4EE5\u4E0A\u7684\u5E16\u5B50\uFF0C\u771F\u5FC3\u63A8\u8350\u7ED9\u5927\u5BB6\u770B\u770B\uFF0C\u770B\u5B8C\u540E\u7EDD\u58C1\u662F\u6709\u6536\u83B7\u7684\u3002</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/szjy/zhihu-1000wan-4612a83f-6207-496c-b32b-c6f1ab031c4f.png" alt=""></p><p>\u867D\u7136\u6709\u4E9B\u57CB\u6CA1\u7684\u5E16\u5B50\u6211\u89C9\u5F97\u4EF7\u503C\u4E5F\u5F88\u9AD8\u3002\u4E0D\u8FC7\uFF0C\u57CB\u6CA1\u5C31\u57CB\u6CA1\u5427\u3002</p><p>\u5B9A\u4E2A\u5C0F\u76EE\u6807\u5427\uFF1A2022 \u5E74\uFF0C\u77E5\u4E4E\u9605\u8BFB\u7834 5000 \u4E07\u9605\u8BFB\uFF08\u597D\u50CF\u6CE8\u5B9A\u5B8C\u4E0D\u6210\uFF09\u3002</p><p>\u8FD9\u4E2A flag \u6211\u7ACB\u8FD9\u4E86\u54C8\uFF0C\u6B22\u8FCE\u5927\u5BB6\u76D1\u7763\u3002\u3002</p><p>\u8981\u662F\u5B8C\u4E0D\u6210\uFF0C\u6211\u628A\u77E5\u4E4E\u4E0A\u7684\u5168\u90E8\u6536\u5165\u62FF\u51FA\u6765\u7ED9\u5927\u5BB6\u5206\u6210\uFF0C\u600E\u4E48\u6837\uFF1F</p><p>\u660E\u5E74\u5E95\u6211\u4F1A\u5728\u8FD9\u91CC\u516C\u5E03\u7ED3\u679C\uFF0C\u5927\u5BB6\u656C\u8BF7\u671F\u5F85\u3002</p><p>\u597D\u4E86\uFF0C\u5C31\u804A\u8FD9\u4E48\u591A\u5427\uFF0C\u7EE7\u7EED\u52A0\u73ED\u56DE\u7B54\u5E16\u5B50\u53BB\u4E86\u3002</p><p>\u6211\u662F\u4E8C\u54E5\uFF0C\u6211\u4EEC\u4E0B\u671F\u89C1~</p>',55),o=[c];function n(s,b){return e(),t("div",null,o)}var d=p(r,[["render",n],["__file","zhihu-1000wan.html.vue"]]);export{d as default};
